import React, { useState } from "react";
import { connect } from "react-redux";
import axios from "axios";
//import PropTypes from "prop-types";

const Creater = () => {
  const [formData, setFormData] = useState({
    order_id: ""
  });

  const { order_id } = formData;

  const onChange = async e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    // let res = await axios.get(
    //   "http://tools.raminternet.com/devotion/api/getProformaInfo?id=" + order_id
    // );
    let body = {
      html:
        '<!DOCTYPE html><html lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width, initial-scale=1.0"><title>Document</title></head><body>ДОКУМЕНТИК</body></html>'
    };
    await axios.post("/api/invoice", body).then(res => {
      let file = new Blob([res.data], { type: "application/pdf" });
    });

    console.log(res);
  };

  return (
    <div>
      <h3>Create Invoice</h3>
      <form className="creater-form" onSubmit={e => onSubmit(e)}>
        <input
          type="text"
          className="creater-input"
          placeholder="Order Id"
          name="order_id"
          value={order_id}
          onChange={e => onChange(e)}
        />
        <input type="submit" className="btn btn-primary" value="LogIn" />
      </form>
    </div>
  );
};
Creater.prototype = {};

//const mapStateToProps = state => ({});

export default connect()(Creater);
