import axios from 'axios';
import { setAlert } from './alert';

import {
  GET_PROFILE,
  PROFILE_ERROR,
  DELETE_ACCOUNT,
  CLEAR_PROFILE,
  GET_PROFILES
} from './types';

export const getCurrentProfile = () => async dispatch => {
  try {
    const res = await axios.get('/api/profile/me');
    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
  } catch (err) {
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.message, status: err.response.status }
    });
  }
};

export const createProfile = ({
  formData,
  history,
  edit = false
}) => async dispatch => {
  try {
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    };
    console.log(formData);
    const res = await axios.post('/api/profile', formData, config);
    dispatch({
      type: GET_PROFILE,
      payload: res.data
    });
    dispatch(
      setAlert(edit ? 'Profile updated' : 'Profile Created', 'success', 1500)
    );
    if (!edit) {
      history.push('/dashboard');
    }
  } catch (err) {
    const errors = err.response.data.errors;
    console.log(errors);
    if (errors) {
      errors.forEach(error => {
        dispatch(setAlert(error.msg, 'danger', 1500));
      });
    }
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.message, status: err.response.status }
    });
  }
};

export const deleteAccount = id => async dispatch => {
  if (window.confirm('Press it hardly')) {
  }
  try {
    const res = await axios.delete('/api/profile');
    dispatch({
      type: CLEAR_PROFILE
    });
    dispatch({
      type: DELETE_ACCOUNT
    });
    dispatch(setAlert('Your account fucked up', 'danger', 1500));
  } catch (err) {
    const errors = err.response.data.errors;
    console.log(errors);
    if (errors) {
      errors.forEach(error => {
        dispatch(setAlert(error.msg, 'danger', 1500));
      });
    }
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.message, status: err.response.status }
    });
  }
};

export const getProfiles = () => async dispatch => {
  dispatch({
    type: CLEAR_PROFILE
  });
  try {
    const res = await axios.get('/api/profile');
    dispatch({
      type: GET_PROFILES
    });
  } catch (err) {
    const errors = err.response.data.errors;
    console.log(errors);
    if (errors) {
      errors.forEach(error => {
        dispatch(setAlert(error.msg, 'danger', 1500));
      });
    }
    dispatch({
      type: PROFILE_ERROR,
      payload: { msg: err.message, status: err.response.status }
    });
  }
};
