import React, { Fragment, Component, Suspense, useEffect } from 'react';
import { Route, Switch, Link } from 'react-router-dom';
import SignIn from './comp/singin/signin';
import LogIn from './comp/singin/login';
import Posts from './comp/posts/posts';
import './style.css';
import { Provider } from 'react-redux';
import store from './store';
import Alert from './comp/alert';
import setAuthToken from './utills/setAuthToken';
import loadUser from './actions/auth';
import Auth from './comp/profile/auth';
import DashBoard from './comp/dashboard/dashboard';
import EditProfile from './comp/dashboard/editProfile';
import PrivateRoute from './routing/privateRoute';
import CreateProfile from './comp/profile/createProfile';
import Profiles from './comp/profile/profiles';
import Profile from './comp/profile/profile';
import openSocket from 'socket.io-client';
import Messenger from './messenger/messenger';

if (localStorage.token) {
  setAuthToken(localStorage.token);
}

const App = () => {

  useEffect(() => {
    store.dispatch(loadUser());
  }, []);

  return (
    <Provider store={store}>
      Test messenger
      <Messenger />
      <div>
        <Auth />
        <Alert />
        <Link to='/signin'>Регистрация</Link>
        <br />
        <Link to='/login'>Войти</Link>
        <br />
        <Link to='/profile'>Профиль</Link>
        <br />
        <Link to='/posts'>Посты</Link>
        <br />
        <br />
        <br />
        <section>
          <Switch>
            <Route path='/login' component={LogIn} />
            <Route path='/signin' component={SignIn} />
            <PrivateRoute path='/posts' component={Posts} />
            <PrivateRoute path='/dashboard' component={DashBoard} />
            <PrivateRoute path='/create-profile' component={CreateProfile} />
            <PrivateRoute path='/edit-profile' component={EditProfile} />
            <PrivateRoute path='/all-profiles' component={Profiles} />
            <PrivateRoute exact path='/profile/:id' component={Profile} />
          </Switch>
        </section>
      </div>
    </Provider>
  );
};

export default App;
