import React, { Fragment, Component, useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import dateFormat from 'dateformat';
const socket = io('http://localhost:5000');

const Chat = ({ chat: { date, messages, users } }) => {
  //TODO вместо id имена. И разделить на полученые и отправленные, как в вк
  //TODO Как вк, при нажатии на сообщение, всплывает диалог, думаю c Route сделать
  date = dateFormat(date);
  messages = messages[0];

  console.log(date);
  console.log(messages);
  console.log(messages.user);
  return (
    <Fragment>
      <div>
        <p>{date}</p>
        <h4>Пользователь: {messages.user}</h4>
        <h3>Сообщение: {messages.text}</h3>
      </div>
    </Fragment>
  );
};

export default Chat;
