import React, { useState, Fragment } from 'react';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import Moment from 'react-moment';
import axios from 'axios';

const Table = () => {
	const [state, setState] = useState({
		invoices: [],
		loading: true,
		redirect: false,
		focus: ''
	});

	const InvoiceTable = () => {
		axios.get('/api/invoice').then(res => {
			setState({ ...state, invoices: res.data, loading: false });
		});
	};
	const editorTable = invoice_id => {
		setState({ ...state, redirect: true, focus: invoice_id });
	};
	const renderRedirect = () => {
		if (state.redirect)
			return <Redirect to={`/invoice/editor/${state.focus}`} />;
	};
	const deleteTable = invoice_id => {
		axios.delete(`/api/invoice/${invoice_id}`);
	};
	const downloadTable = invoice_id => {
		console.log('Download action');
	};

	return (
		<div>
			{InvoiceTable()}
			{renderRedirect()}
			<Fragment>
				{state.loading ? (
					<p>Загрузка...</p>
				) : (
					<Fragment>
						{state.invoices.map(item => (
							<div key={item._id}>
								<div>{item.invoice_id}</div>
								<div>{item.order_id}</div>
								<div>
									<Moment format="YYYY/MM/DD">
                {item.created_at}</Moment>
								</div>
								<div className='btn table-btn' onClick={() => editorTable(item.invoice_id)}>Edit</div>
								<div className='btn table-btn' onClick={() => deleteTable(item.invoice_id)}>Delete</div>
								<div className='btn table-btn' onClick={() => downloadTable(item.invoice_id)}>
									Download
								</div>
							</div>
						))}
					</Fragment>
				)}
			</Fragment>
		</div>
	);
};
Table.prototype = {};

export default connect()(Table);
