import React, { useState } from 'react';
import { connect } from 'react-redux';
import InvoiceContainer from './invoiceContainer.js';
import axios from 'axios';
import { saveAs } from 'file-saver';
//import PropTypes from "prop-types";

const Creater = () => {
	const [state, setState] = useState({
		order_id: '',
		items: [],
		invoice_id: '',
		currency: '',
		data: [],
		loaded: false
	});

	const onChange = async e => {
		setState({ ...state, [e.target.name]: e.target.value });
	};

	const onSubmit = async e => {
		e.preventDefault();
	};

	return (
		<div className='creater-container'>
			<Fragment>
				{state.loading ? (
					<p>Загрузка...</p>
				) : (
					<Fragment>
						{state.invoices.map(item => (
							<div>
								<h4 key={item._id}>{item.invoice_id}</h4>
								<div onClick={() => editorTable(item.invoice_id)}>Edit</div>
								<div onClick={() => deleteTable(item.invoice_id)}>Delete</div>
								<div onClick={() => downloadTable(item.invoice_id)}>
									Download
								</div>
							</div>
						))}
					</Fragment>
				)}
			</Fragment>
		</div>
	);
};
Creater.prototype = {};

//const mapStateToProps = state => ({});

export default connect()(Creater);
