import React, { useState, Fragment } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
//import PropTypes from "prop-types";

const Table = () => {
	const [state, setState] = useState({
		invoices: [],
		loading: false
	});

	const InvoiceTable = () => {
		axios.get('/api/invoice').then(data => {
			console.log(data);
			setState({ ...state, invoices: data, loading: true });
		});
	};

	return (
		<div>
			{InvoiceTable()}
			<Fragment>
				{state.loading ? (
					<p>Загрузка...</p>
				) : (
					<Fragment>
						{state.invoices.map(item => (
							<h4 key={item._id}>{item.invoice_id}</h4>
						))}
					</Fragment>
				)}
			</Fragment>
		</div>
	);
};
Table.prototype = {};

// const mapStateToProps = state => ({
//   auth: state.auth
// });

export default connect()(Table);
