import React, { Fragment, Component, useEffect, useState } from 'react';
import io from 'socket.io-client';
const socket = io('http://localhost:5000');

const Messenger = () => {
  const [state, setState] = useState({
    clients: []
  });

  socket.on('connect', () => {
    if (localStorage.token) {
      socket.emit('token', { token: localStorage.token });
    }
  });
  socket.on('allClients', clients => {
    setState({ ...state, clients: clients.clients });
  });

  socket.on('privateMsgClient', msg => {
    console.log(msg.msg);
  });
  const selectSend = client => {
    console.log(client);
    socket.emit('privateMsgServer', { to: client, msg: 'Пошел нахуй' });
  };

  const { clients } = state;
  console.log(clients);
  return (
    <Fragment>
      {clients === null ? (
        <p>Клиентов нет</p>
      ) : (
        <Fragment>
          {clients.map((client, index) => (
            <p key={index} onClick={() => selectSend(client)} value={client}>
              {client}
            </p>
          ))}
        </Fragment>
      )}
    </Fragment>
  );
};

export default Messenger;
