import React, { useState, Fragment } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
//import PropTypes from "prop-types";

const Table = () => {
	const [state, setState] = useState({
		invoices: [],
		loading: true
	});

	const InvoiceTable = () => {
		axios.get('/api/invoice').then(res => {
			setState({ ...state, invoices: res.data, loading: false });
		});
	};

	return (
		<div>
			{InvoiceTable()}
			<Fragment>
				{state.loading ? (
					<p>Загрузка...</p>
				) : (
					<Fragment>
						{state.invoices.map(item => (
							<div>
								<h4 key={item._id}>{item.invoice_id}</h4>
								<div onClick={() => editorTable(item.invoice_id)}>
									Редактировать
								</div>
								<div onClick={() => deleteTable(item.invoice_id)}>Удалить</div>
								<div onClick={() => downloadTable(item.invoice_id)}>
									Скачать
								</div>
							</div>
						))}
					</Fragment>
				)}
			</Fragment>
		</div>
	);
};
Table.prototype = {};

// const mapStateToProps = state => ({
//   auth: state.auth
// });

export default connect()(Table);
