import io from 'socket.io-client';
const socket = openSocket('http://localhost:5000');

const messenger = () => {
  socket.on('connect', () => {
    console.log(socket.connected);
    socket.emit('end', { msg: 'end' });
  });
  socket.on('start', msg => {
    console.log('MSG - ', msg);
  });
};

export default messenger;
