import React, { Fragment, Component, useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import Chats from './chats';
const socket = io('http://localhost:5000');

const Messenger = () => {
  const [state, setState] = useState({
    currentClient: '',
    currentUser: [],
    clients: [],
    chats: [],
    loading: true
  });

  window.onunload = () => {
    socket.emit('client_disconnected', { socket: state.currentClient });
  };
  socket.on('connect', async clients => {});

  socket.on('getAllUsers', async msg => {
    const currentUser = await msg.users.filter(user => {
      return user.socket === state.currentClient;
    });

    setState({
      ...state,
      clients: msg.users,
      loading: false,
      currentUser: currentUser[0]
    });
  });

  socket.on('allClients', clients => {
    setState({ ...state, clients: clients.clients });
  });

  socket.on('socketId', msg => {
    if (localStorage.token) {
      setState({ ...state, currentClient: msg.socketId });
      socket.emit('token', {
        token: localStorage.token,
        socketId: msg.socketId
      });
    }
  });

  socket.on('privateMsgClient', msg => {
    console.log(msg.msg);
  });

  const selectSend = async (socket_id, name, id, online) => {
    const msg = await prompt(`Отправить письмо ${name}`, '');
    socket.emit('privateMsgServer', {
      from: state.currentUser._id,
      to: socket_id,
      msg: msg,
      name: name,
      id: id
    });
  };

  const refresh = async () => {
    try {
      const users = await axios.get('/api/users/all');
      setState({ ...state, clients: users.data });
    } catch (err) {
      console.error(err);
    }
  };

  const { clients, loading } = state;
  console.log(clients);
  return (
    <Fragment>
      {loading ? (
        <p>Загрузка...</p>
      ) : (
        <Fragment>
          {clients.map(client => (
            <h4
              key={client._id}
              onClick={() =>
                selectSend(
                  client.socket,
                  client.name,
                  client._id,
                  client.online
                )
              }
            >
              {client.name}
              {client.online ? <span> Online</span> : <span> Offline</span>}
            </h4>
          ))}
        </Fragment>
      )}
      <div className="users-cont"></div>


      <h5 onClick={() => refresh()}>ОБНОВИТЬ</h5>
      <Chats />
    </Fragment>
  );
};

export default Messenger;
