import React, { useState } from 'react';
import { connect } from 'react-redux';
import axios from 'axios';
import { saveAs } from 'file-saver';
//import PropTypes from "prop-types";

const Creater = () => {
	const [formData, setFormData] = useState({
		order_id: ''
	});

	const { order_id } = formData;

	const onChange = async e => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};

	const onSubmit = async e => {
		e.preventDefault();
		let res = await axios.get(
			`http://tools.raminternet.com/devotion/api/getProformaInfo/?id=${order_id}%7BorderId%7D&token=n9VTU1yvRomDHZOBfTt0aKEMlSmzO9Na`
		);
		// await axios
		// 	.get('/api/invoice', {
		// 		responseType: 'arraybuffer',
		// 		headers: { Accept: 'application/pdf' }
		// 	})
		// 	.then(res => {
		// 		const blob = new Blob([res.data], { type: 'application/pdf' });
		// 		saveAs(blob, `pdf${order_id}.pdf`);
		// 	});
		// await axios
		// 	.get('/api/invoice/file/' + order_id, { response_type: 'blob' })
		// 	.then(res => {
		// 		console.log(res.data);
		// 		let file = new Blob([res.data], { type: 'application/pdf' });
		// 		saveAs(file, `pdf${order_id}.pdf`);
		// 	});
	};

	return (
		<div>
			<h3>Create Invoice</h3>
			<form className='creater-form' onSubmit={e => onSubmit(e)}>
				<input
					type='text'
					className='creater-input'
					placeholder='Order Id'
					name='order_id'
					value={order_id}
					onChange={e => onChange(e)}
				/>
				<input type='submit' className='btn btn-primary' value='LogIn' />
			</form>
		</div>
	);
};
Creater.prototype = {};

//const mapStateToProps = state => ({});

export default connect()(Creater);
