import React from 'react';
import { connect } from 'react-redux';
//import PropTypes from "prop-types";

const InvoiceContainer = props => {
	const {
		invoice_id,
		loaded,
		currency,
		payment_reference,
		issue_date,
		customer_address,
		delivery_address,
		items
	} = props;

	const onChange = async e => {
		props.onChange(e);
	};

	const onSubmit = async e => {
		e.preventDefault();
	};

	const InvoicePrototype = () => {
		if (loaded) {
			return (
				<form className='proto-form' onSubmit={e => onSubmit(e)}>
					<input
						type='text'
						className='proto-input'
						name='invoice_id'
						value={invoice_id}
						onChange={e => onChange(e)}
					/>
					<br />
					<input
						type='text'
						className='proto-input'
						name='currency'
						value={currency}
						onChange={e => onChange(e)}
					/>
					<br />
					<input
						type='text'
						className='proto-input'
						name='issue_date'
						value={issue_date}
						onChange={e => onChange(e)}
					/>
					<br />
					<input
						type='text'
						className='proto-input'
						name='payment_reference'
						value={payment_reference}
						onChange={e => onChange(e)}
					/>
					<br />
					<textarea
						rows='7'
						cols='30'
						name='customer_address'
						onChange={e => onChange(e)}
						defaultValue={customer_address}
					></textarea>
					<br />
					<textarea
						rows='7'
						cols='30'
						name='delivery_address'
						onChange={e => onChange(e)}
						defaultValue={delivery_address}
					></textarea>
					<input type='submit' className='btn btn-primary' value='LogIn' />
				</form>
			);
		}
	};

	return <div className='invoice-prototype'>{InvoicePrototype()}</div>;
};
InvoiceContainer.prototype = {};

//const mapStateToProps = state => ({});

export default connect()(InvoiceContainer);
