import React from 'react';
import { connect } from 'react-redux';

const InvoiceContainer = props => {
	const {
		invoice_id,
		loading,
		currency,
		payment_reference,
		issue_date,
		customer_address,
		delivery_address
	} = props;

	const onChange = async e => {
		console.log(customer_address);
		props.onChange(e);
	};

	const onSave = () => {
		props.onSave();
	};

	const onSaveAndDownload = () => {
		props.onSaveAndDownload();
	};

	const InvoicePrototype = () => {
		if (!loading) {
			return (
				<div>
					<form className='proto-form'>
						<input
							type='text'
							className='proto-input'
							name='invoice_id'
							value={invoice_id}
							onChange={e => onChange(e)}
						/>
						<br />
						<input
							type='text'
							className='proto-input'
							name='currency'
							value={currency}
							onChange={e => onChange(e)}
						/>
						<br />
						<input
							type='text'
							className='proto-input'
							name='issue_date'
							value={issue_date}
							onChange={e => onChange(e)}
						/>
						<br />
						<input
							type='text'
							className='proto-input'
							name='payment_reference'
							value={payment_reference}
							onChange={e => onChange(e)}
						/>
						<br />
						<textarea
							rows='7'
							cols='30'
							name='customer_address'
							onChange={e => onChange(e)}
							defaultValue={customer_address}
						></textarea>
						<br />
						<textarea
							rows='7'
							cols='30'
							name='delivery_address'
							onChange={e => onChange(e)}
							defaultValue={delivery_address}
						></textarea>
					</form>
					<div onClick={() => onSave()} className='btn-save'>
						Update
					</div>
					<div
						onClick={() => onSaveAndDownload()}
						className='btn-save-download'
					>
						Update & Download
					</div>
				</div>
			);
		}
	};

	return <div className='invoice-prototype'>{InvoicePrototype()}</div>;
};
InvoiceContainer.prototype = {};

export default connect()(InvoiceContainer);
