import io from 'socket.io-client';
const socket = io('http://localhost:5000');

const messenger = () => {
  socket.on('connect', () => {
    console.log('Server Connected');
  });
  socket.emit('end', { msg: 'end' });
  socket.on('allClients', msg => {
    console.log(msg);
  });
};

export default messenger;
