import React, { Fragment, Component, Suspense, useEffect } from 'react';
import { Route, Switch, Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { getCurrentProfile } from '../../actions/profile';
import profile from '../../reducers/profile';
import PropTypes from 'prop-types';
import Loading from '../loading/loading';

const DashBoard = ({
  getCurrentProfile,
  auth,
  profile: { profile, loading }
}) => {
  useEffect(() => {
    getCurrentProfile();
  }, []);

  return loading && profile === null ? (
    <Loading />
  ) : (
    <Fragment>
      Спасибо что зашел, {auth.user.name}
      <br />
      <br />
      {profile !== null ? (
        <Fragment>Вот твой профиль</Fragment>
      ) : (
        <Fragment>
          У тоби нит профелия <br />
          <Link to='/create-profile'>Мы можем это исправить - ТЫкай</Link>
        </Fragment>
      )}
    </Fragment>
  );
};

DashBoard.prototype = {
  getCurrentProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  profile: state.profile,
  auth: state.auth
});

export default connect(
  mapStateToProps,
  {
    getCurrentProfile
  }
)(DashBoard);
